import { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "./Login/Login.css";

const LoginScreen = ({ history }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  useEffect(() => {
    if (localStorage.getItem("authToken")) {
      history.push("/");
    }
  }, [history]);

  const loginHandler = async (e) => {
    e.preventDefault();

    const config = {
      header: {
        "Content-Type": "application/json",
      },
    };

    try {
      const { data } = await axios.post(
        "/api/auth/login",
        { email, password },
        config
      );

      localStorage.setItem("authToken", data.token);

      history.push("/");
    } catch (error) {
      setError(error.response.data.error);
      setTimeout(() => {
        setError("");
      }, 5000);
    }
  };

  return (
    <>
      {/* // <div className="login-screen">
    //   <form onSubmit={loginHandler} className="login-screen__form">
    //     <h3 className="login-screen__title">Login</h3>
    //     {error && <span className="error-message">{error}</span>}
    //     <div className="form-group">
    //       <label htmlFor="email">Email:</label>
    //       <input
    //         type="email"
    //         required
    //         id="email"
    //         placeholder="Email address"
    //         onChange={(e) => setEmail(e.target.value)}
    //         value={email}
    //         tabIndex={1}
    //       />
    //     </div>
    //     <div className="form-group">
    //       <label htmlFor="password">
    //         Password:{" "}
          
    //       </label>
    //       <input
    //         type="password"
    //         required
    //         id="password"
    //         autoComplete="true"
    //         placeholder="Enter password"
    //         onChange={(e) => setPassword(e.target.value)}
    //         value={password}
    //         tabIndex={2}
    //       />
    //     </div>
    //     <button type="submit" className="btn btn-primary">
    //       Login
    //     </button>

    //     <span className="login-screen__subtext">
    //       Don't have an account? <Link to="/register">Register</Link>
    //     </span>
    //   </form>
    // </div>
 */}


      <section id='Login'>
        <div className="loginCard">
          <div className="loginText">
            <h1>Login</h1>
          </div>

          <form action="" onSubmit={loginHandler}>
            <input type="email" placeholder='Enter your email' onChange={(e) => setEmail(e.target.value)} value={email} tabIndex={1} />
            <input type="password" placeholder='Confirm a password' onChange={(e) => setPassword(e.target.value)} value={password} tabIndex={2} />


            <div className="check">
              {/* <input type="checkbox" /> */}
              {/* <p>Remember me</p> */}
              <a href="#">Forget Password?</a>
            </div>

            <button type="submit">Login Now</button>

            <p>Don't have an account? <Link to="/register">Register</Link></p>

          </form>



        </div>
      </section>


    </>
  );
};

export default LoginScreen;
